Script.script:
group = {} 

function group_add_member(name)
  local member = group[name]
  if member then
    echo(name .. " is already in the group.")
    return
  end
  group[name] = true  -- Not needed, just to balance table.
end

function group_remove_member(name)
  group[name] = nil
  echo(name .. " removed from group")
end

function group_show_group()
  echo("Group members:")
  for name, unused in pairs(group) do
    echo(name)
  end
end

ScriptGroup.script:
function initialize_spell_timer()
	local spell_timer = {}
	spell_timer.timers = {}
	return spell_timer
end

spell_timer = spell_timer or initialize_spell_timer()

spell_timer.group = "Spell Timer"

spell_timer.colour = "cyan"
spell_timer.colour = "grey"

