function spell_timer.get_trigger_stop_code(spell) local code = [[ local <NAME> = spell_timer.get_timer("<NAME>")
<NAME>.stop_time = time_get_time() <NAME>.last_elapsed_time = <NAME>.elapsed_time <NAME>.elapsed_time = <NAME>.stop_time - <NAME>.start_time
local time = time.convert_seconds_to_hms(<NAME>.elapsed_time) echo("\nThe spell lasted " .. time .. ".\n") ]] code = string.gsub(code, "<NAME>", spell.name) return code end

function spell_timer.truncate(exact)
	return tonumber(string.format("%.3f", exact))
end

clock_season = { "Winter", "Winter", "Winter", "Spring", "Spring", "Spring", "Summer", "Summer", "Summer", "Autumn", "Autumn", "Autumn", }
clock_month_of_season = { "1", "2", "3", "1", "2", "3", "1", "2", "3", "1", "2", "3", }
--clock_month_of_season = { -- "first", -- "second", -- "third", -- "first", -- "second", -- "third", -- "first", -- "second", -- "third", -- "first", -- "second", -- "third", --}

function clock_get_season(mume_moment)
	return clock_season[mume_moment.month]
end

function clock_get_month_of_season(mume_moment)
	return clock_month_of_season[mume_moment.month]
end

function clock_get_highlight()
	return "<" .. clock_options_highlight .. ">"
end

function time_get_time()
	local current_time_as_string = getTime(true)

	return datetime:parse(current_time_as_string, "%Y.%m.%d %H:%M:%S", true)
end

function UsePowerRing(R)
send("get "..R.." pouch;rem "..theRing..";wear "..R..";use "..R..";rem "..R..";wear "..theRing..";put "..R.." pouch")
end

